import React, { useState } from "react";
import axios from "axios";

const CreateRoom = () => {
  const [type, setType] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [capacity, setCapacity] = useState("");
  const [petsAllowed, setPetsAllowed] = useState(false);
  const [facilities, setFacilities] = useState([]);
  const [images, setImages] = useState({
    image1: null,
    image2: null,
    image3: null,
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleFacilityChange = (e) => {
    const { value } = e.target;
    if (!facilities.includes(value)) {
      setFacilities([...facilities, value]);
    } else {
      setFacilities(facilities.filter((f) => f !== value));
    }
  };

  const handleImageChange = (e, imageKey) => {
    setImages((prevImages) => ({
      ...prevImages,
      [imageKey]: e.target.files[0],
    }));
  };

  const resetForm = () => {
    setType("");
    setDescription("");
    setPrice("");
    setCapacity("");
    setPetsAllowed(false);
    setFacilities([]);
    setImages({ image1: null, image2: null, image3: null });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    const formData = new FormData();
    formData.append("typeRoom", type);
    formData.append("description", description);
    formData.append("pricePerDay", price);
    formData.append("maxCapacity", capacity);
    formData.append("allowPet", petsAllowed);
    facilities.forEach((facility, index) => {
      formData.append(`facilities[${index}]`, facility);
    });
    formData.append("images", images.image1);
    formData.append("images", images.image2);
    formData.append("images", images.image3);

    try {
      const response = await axios.post(
        "http://localhost:8080/hotelManagement/rooms/create-room",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log("Room created successfully:", response.data);
      setMessage("Room created successfully!");
      resetForm();
    } catch (error) {
      console.error("Error creating room:", error);
      setMessage("Error creating room. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-room-container">
      <h2>Create Room</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <div className="create-room-form-group">
          <label htmlFor="type">Type of Room</label>
          <select
            id="type"
            value={type}
            onChange={(e) => setType(e.target.value)}
          >
            <option value="">Select type</option>
            <option value="single">Single</option>
            <option value="double">Double</option>
            <option value="suite">Suite</option>
          </select>
        </div>
        <div className="create-room-form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
          ></textarea>
        </div>
        <div className="create-room-form-group">
          <label htmlFor="price">Price</label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <div className="create-room-form-group">
          <label htmlFor="capacity">Max Capacity</label>
          <input
            type="number"
            id="capacity"
            value={capacity}
            onChange={(e) => setCapacity(e.target.value)}
          />
        </div>
        <div className="create-room-form-group">
          <label>
            <input
              type="checkbox"
              checked={petsAllowed}
              onChange={(e) => setPetsAllowed(e.target.checked)}
            />
            Pets Allowed
          </label>
        </div>
        <div className="create-room-form-group">
          <label htmlFor="facilities">Facilities</label>
          <div className="create-room-facilities">
            {["wifi", "parking", "pool", "gym", "spa"].map((facility) => (
              <label key={facility}>
                <input
                  type="checkbox"
                  value={facility}
                  checked={facilities.includes(facility)}
                  onChange={handleFacilityChange}
                />
                {facility.charAt(0).toUpperCase() + facility.slice(1)}
              </label>
            ))}
          </div>
        </div>
        <div className="create-room-form-group">
          <label>Upload Images</label>
          <div className="create-room-images-upload">
            <input
              type="file"
              onChange={(e) => handleImageChange(e, "image1")}
            />
            <input
              type="file"
              onChange={(e) => handleImageChange(e, "image2")}
            />
            <input
              type="file"
              onChange={(e) => handleImageChange(e, "image3")}
            />
          </div>
        </div>
        <button
          type="submit"
          className="create-room-submit-btn"
          disabled={loading}
        >
          {loading ? "Creating..." : "Create Room"}
        </button>
      </form>
    </div>
  );
};

export default CreateRoom;
