import React, { useState, useEffect } from "react";

const UserManage = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    // Fetch users from the API or data source
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    // Fetch users from an API and set the users state
    // For demonstration, we use a hardcoded list
    const usersData = [
      {
        email: "john.doe@example.com",
        fullName: "John Doe",
        phoneNumber: "123-456-7890",
        address: "123 Main St",
        role: "user",
        city: "New York",
        country: "USA",
      },
      {
        email: "jane.doe@example.com",
        fullName: "Jane Doe",
        phoneNumber: "987-654-3210",
        address: "456 Elm St",
        role: "admin",
        city: "San Francisco",
        country: "USA",
      },
    ];
    setUsers(usersData);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleUpdate = (email) => {
    // Handle update user logic
    console.log(`Update user with email: ${email}`);
  };

  const handleDelete = (email) => {
    // Handle delete user logic
    setUsers(users.filter((user) => user.email !== email));
  };

  const handleAddUser = () => {
    // Handle add user logic
    console.log("Add new user");
  };

  const filteredUsers = users.filter((user) =>
    user.fullName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="user-manage">
      <div className="user-manage-header">
        <h1>User Management</h1>
        <button className="btn-add-user" onClick={handleAddUser}>
          Add New User
        </button>
      </div>
      <div className="user-manage-search">
        <input
          type="text"
          placeholder="Search by full name..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <table className="user-manage-table">
        <thead>
          <tr>
            <th>Email</th>
            <th>Full Name</th>
            <th>Phone Number</th>
            <th>Address</th>
            <th>Role</th>
            <th>City</th>
            <th>Country</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user.email}>
              <td>{user.email}</td>
              <td>{user.fullName}</td>
              <td>{user.phoneNumber}</td>
              <td>{user.address}</td>
              <td>{user.role}</td>
              <td>{user.city}</td>
              <td>{user.country}</td>
              <td>
                <button
                  className="btn-update"
                  onClick={() => handleUpdate(user.email)}
                >
                  Update
                </button>
                <button
                  className="btn-delete"
                  onClick={() => handleDelete(user.email)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserManage;
