import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function Room() {
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const token = "YOUR_AUTH_TOKEN"; // Thay YOUR_AUTH_TOKEN bằng token xác thực của bạn
      const response = await fetch("/rooms/get-all-rooms", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();

      if (response.ok) {
        setRooms(data);
      }
    } catch (error) {
      console.error("Error fetching rooms:", error);
    }
  };

  return (
    <div className="room-list">
      {rooms.map((room) => (
        <article className="room" key={room.id}>
          <div className="img-container">
            <img src={room.images[0]} alt="single room" />

            <div className="price-top">
              <h6>$ {room.pricePerDay}</h6>
              <p>per night</p>
            </div>

            <Link to={`/rooms/${room.id}`} className="btn-primary room-link">
              View
            </Link>
          </div>
          <p className="room-info">{room.typeRoom}</p>
          <p className="room-description">{room.description}</p>
        </article>
      ))}
    </div>
  );
}
