import React, { useState, useEffect } from "react";

const EditUserPage = () => {
  const [user, setUser] = useState({
    email: "",
    fullName: "",
    phoneNumber: "",
    address: "",
    city: "",
    country: "",
  });
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    // Lấy thông tin người dùng hiện tại từ API và điền vào các trường
    fetch("http://localhost:8080/hotelManagement/user/my-info/edit")
      .then((response) => response.json())
      .then((data) => {
        setUser(data.Info);
      })
      .catch((error) => {
        setErrorMessage("Đã xảy ra lỗi khi tải thông tin người dùng.");
      });
  }, []);

  const handleChange = (e) => {
    setUser({
      ...user,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("/user/my-info/edit", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          setErrorMessage(data.error);
          setSuccessMessage("");
        } else {
          setSuccessMessage(data.message);
          setErrorMessage("");
        }
      })
      .catch((error) => {
        setErrorMessage("Đã xảy ra lỗi khi cập nhật thông tin người dùng.");
        setSuccessMessage("");
      });
  };

  return (
    <div className="container-edit">
      <h1>Chỉnh sửa thông tin</h1>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      {successMessage && (
        <div className="success-message">{successMessage}</div>
      )}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={user.email}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="fullName">Họ và tên:</label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={user.fullName}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="phoneNumber">Số điện thoại:</label>
          <input
            type="tel"
            id="phoneNumber"
            name="phoneNumber"
            value={user.phoneNumber}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Địa chỉ:</label>
          <input
            type="text"
            id="address"
            name="address"
            value={user.address}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="city">Thành phố:</label>
          <input
            type="text"
            id="city"
            name="city"
            value={user.city}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="country">Quốc gia:</label>
          <input
            type="text"
            id="country"
            name="country"
            value={user.country}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <button type="submit" className="btn-submit">
          Cập nhật
        </button>
      </form>
    </div>
  );
};

export default EditUserPage;
