import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import defaultImg from "../../assets/img/jpeg/room-1.jpeg";

export default function Room() {
  const [rooms, setRooms] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        "http://localhost:8080/hotelManagement/rooms",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();

      if (response.ok) {
        console.log(data);
        setRooms(data);
      } else {
        console.error("Error fetching rooms:", data);
      }
    } catch (error) {
      console.error("Error fetching rooms:", error);
    }
  };

  useEffect(() => {
    if (rooms.length > 0) {
      const room = rooms.find((room) => room.id === id);
      setSelectedRoom(room);
    }
  }, [rooms, id]);

  if (selectedRoom === null) {
    return <p>Loading...</p>;
  }

  return (
    <article className="room">
      <div className="img-container">
        <img src={defaultImg} alt="single room" />

        <div className="price-top">
          <h6>$ {selectedRoom.pricePerDay}</h6>
          <p>per night</p>
        </div>

        <Link
          to={`/rooms/${selectedRoom.id}`}
          className="btn-primary room-link"
        >
          View
        </Link>
      </div>
      <p className="room-info">{selectedRoom.typeRoom}</p>
      {/* <p className="room-description">{selectedRoom.description}</p> */}
    </article>
  );
}
