import React, { Component } from "react";

// imports react-icons
import { FaCocktail, FaHiking, FaShuttleVan, FaBeer } from "react-icons/fa";

// imports components
import Title from "../Title/Title";

export default class Services extends Component {
  state = {
    services: [
      {
        icon: <FaCocktail />,
        title: "free cocktails",
        info: "Unwind with complimentary cocktails at our hotel. Cheers to a memorable stay!",
      },
      {
        icon: <FaHiking />,
        title: "endless hiking",
        info: "Discover nature's wonders on endless trails of adventure and serenity.",
      },
      {
        icon: <FaShuttleVan />,
        title: "free shuttle",
        info: "hahahaha hahaha hahahahahah hhahaha hahah hahah hahah hahahhha hahaha hahaha hahah",
      },
      {
        icon: <FaBeer />,
        title: "storages beer",
        info: "hahahaha hahaha hahahahahah hhahaha hahah hahah hahah hahahhha hahaha hahaha hahah",
      },
    ],
  };

  render() {
    return (
      <section className="services">
        <Title title="services" />

        <div className="services-center">
          {this.state.services.map((item, index) => {
            return (
              <article key={index} className="services">
                <span>{item.icon}</span>
                <h6>{item.title}</h6>
                <p>{item.info}</p>
              </article>
            );
          })}
        </div>
      </section>
    );
  }
}
