import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import defaultImg from "../../assets/img/jpeg/room-1.jpeg";

export default function Room() {
  const [room, setRoom] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    fetchRoom();
  }, [id]);

  const fetchRoom = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:8080/hotelManagement/rooms/${id}`,
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();

      if (response.ok) {
        console.log(data);
        setRoom(data);
      } else {
        console.error("Error fetching room:", data);
      }
    } catch (error) {
      console.error("Error fetching room:", error);
    }
  };

  if (room === null) {
    return <p>Loading...</p>;
  }

  return (
    <article className="room">
      <div className="img-container">
        <img src={defaultImg} alt="single room" />

        <div className="price-top">
          <h6>$ {room.pricePerDay}</h6>
          <p>per night</p>
        </div>

        <Link to={`/rooms/${room.id}`} className="btn-primary room-link">
          View
        </Link>
      </div>
      <p className="room-info">{room.typeRoom}</p>
      {/* <p className="room-description">{room.description}</p> */}
    </article>
  );
}
