import React, { useState } from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";

const LoginForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const userLogin = {
      userName: username,
      password: password,
    };

    fetch("http://localhost:8080/hotelManagement/auth/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userLogin),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          setError(data.error);
        } else {
          const token = data.token;
          localStorage.setItem("token", token);
          alert(data.msg);

          // Thực hiện chuyển hướng hoặc các hoạt động khác sau khi đăng nhập thành công
        }
      })
      .catch((error) => {
        setError(error.message);
      });
  };

  //   return (
  //     <div className="container">
  //       <h1 className="login-form__title">Đăng nhập</h1>
  //       {error && <div className="login-form__error-message">{error}</div>}
  //       <form className="login-form" onSubmit={handleSubmit}>
  //         <div>
  //           <label className="login-form__label" htmlFor="username">
  //             Username:
  //           </label>
  //           <input
  //             className="login-form__input"
  //             type="text"
  //             id="username"
  //             value={username}
  //             onChange={handleUsernameChange}
  //           />
  //         </div>
  //         <div>
  //           <label className="login-form__label" htmlFor="password">
  //             Password:
  //           </label>
  //           <input
  //             className="login-form__input"
  //             type="password"
  //             id="password"
  //             value={password}
  //             onChange={handlePasswordChange}
  //           />
  //         </div>
  //         <button className="login-form__button" type="submit">
  //           Đăng nhập
  //         </button>
  //       </form>
  //     </div>
  //   );
  return (
    <div className="container">
      <h1 className="login-form__title">Đăng nhập</h1>
      {error && <div className="login-form__error-message">{error}</div>}
      <form className="login-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="login-form__label" htmlFor="username">
            Username:
          </label>
          <input
            className="login-form__input"
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
          />
        </div>
        <div className="form-group">
          <label className="login-form__label" htmlFor="password">
            Password:
          </label>
          <input
            className="login-form__input"
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>
        <button className="login-form__button" type="submit">
          Đăng nhập
        </button>
      </form>
      <div className="register-link">
        Bạn chưa có tài khoản? <Link to="/register">Đăng kí</Link>{" "}
        {/* Sử dụng Link nếu bạn sử dụng routing */}
        {/* Hoặc sử dụng button nếu muốn chuyển hướng trực tiếp */}
        {/* <button onClick={() => { window.location.href = '/register'; }}>Đăng kí</button> */}
      </div>
    </div>
  );
};

export default LoginForm;
