import React, { useState } from "react";

const CreateRoom = () => {
  const [type, setType] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [capacity, setCapacity] = useState("");
  const [petsAllowed, setPetsAllowed] = useState(false);
  const [facilities, setFacilities] = useState([]);
  const [images, setImages] = useState({
    image1: null,
    image2: null,
    image3: null,
  });

  const handleFacilityChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setFacilities([...facilities, value]);
    } else {
      setFacilities(facilities.filter((facility) => facility !== value));
    }
  };

  return (
    <div className="create-room-container">
      <h2>Create Room</h2>
      <form>
        <div className="create-room-form-group">
          <label htmlFor="type">Type of Room</label>
          <select
            id="type"
            value={type}
            onChange={(e) => setType(e.target.value)}
          >
            <option value="">Select type</option>
            <option value="single">Single</option>
            <option value="double">Double</option>
            <option value="suite">Suite</option>
          </select>
        </div>
        <div className="create-room-form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
          ></textarea>
        </div>
        <div className="create-room-form-group">
          <label htmlFor="price">Price</label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <div className="create-room-form-group">
          <label htmlFor="capacity">Max Capacity</label>
          <input
            type="number"
            id="capacity"
            value={capacity}
            onChange={(e) => setCapacity(e.target.value)}
          />
        </div>
        <div className="create-room-form-group">
          <label>
            <input
              type="checkbox"
              checked={petsAllowed}
              onChange={(e) => setPetsAllowed(e.target.checked)}
            />
            Pets Allowed
          </label>
        </div>
        <div className="create-room-form-group">
          <label htmlFor="facilities">Facilities</label>
          <div className="create-room-facilities">
            {["wifi", "parking", "pool", "gym", "spa"].map((facility) => (
              <label key={facility}>
                <input
                  type="checkbox"
                  value={facility}
                  checked={facilities.includes(facility)}
                  onChange={handleFacilityChange}
                />
                {facility.charAt(0).toUpperCase() + facility.slice(1)}
              </label>
            ))}
          </div>
        </div>
        <div className="create-room-form-group">
          <label>Upload Images</label>
          <div className="create-room-images-upload">
            <input
              type="file"
              onChange={(e) =>
                setImages({ ...images, image1: e.target.files[0] })
              }
            />
            <input
              type="file"
              onChange={(e) =>
                setImages({ ...images, image2: e.target.files[0] })
              }
            />
            <input
              type="file"
              onChange={(e) =>
                setImages({ ...images, image3: e.target.files[0] })
              }
            />
          </div>
        </div>
        <button type="submit" className="create-room-submit-btn">
          Create Room
        </button>
      </form>
    </div>
  );
};

export default CreateRoom;
