import "./App.css";
import { Route, Switch, Redirect } from "react-router-dom";
import Home from "../Pages/Home";
import Room from "../Pages/Room";
import SingleRoom from "../Pages/SingleRoom";
import Register from "../Pages/Register";
import Login from "../Pages/Login";
import Admin from "../Pages/Admin";
import Error from "../Pages/Error";
import EditUser from "../Pages/EditUser";
import Navbar from "../Components/Navbar/Navbar";
import Footer from "../Components/Footer/Footer";

function App() {
  const userRole = localStorage.getItem("role");

  return (
    <>
      {/* Render admin interface if the user is an admin */}
      {userRole === "ADMIN" && <Route path="/admin" component={Admin} />}

      {/* Render user interface if the user is not an admin */}
      {userRole !== "ADMIN" && (
        <>
          <Navbar />
          <Switch>
            <Route exact path="/" component={Home} />
            <Route exact path="/rooms/" component={Room} />
            <Route exact path="/rooms/:id" component={SingleRoom} />
            <Route path="/register" component={Register} />
            <Route path="/login" component={Login} />
            <Route path="/edit" component={EditUser} />
            <Route path="/edit/:email" component={EditUser} />
            <Route component={Error} />
          </Switch>
          <Footer />
        </>
      )}

      {/* Redirect to the appropriate interface based on user's role */}
      {userRole ? (
        userRole === "ADMIN" ? (
          <Redirect to="/admin" />
        ) : (
          <Redirect to="/" />
        )
      ) : (
        <Redirect to="/login" />
      )}
    </>
  );
}

export default App;
