import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import defaultImg from "../../assets/img/jpeg/room-1.jpeg";

export default function Room() {
  const { id } = useParams();
  const [room, setRoom] = useState(null);

  useEffect(() => {
    fetchRoom();
  }, [id]);

  const fetchRoom = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:8080/hotelManagement/rooms/get-room/${id}`,
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();

      if (response.ok) {
        setRoom(data);
      } else {
        console.error("Error fetching room:", data);
      }
    } catch (error) {
      console.error("Error fetching room:", error);
    }
  };

  if (!room) {
    return <div>Loading...</div>;
  }

  return (
    <div className="room">
      <div className="img-container">
        <img
          src={
            room.images && room.images.length > 0 ? room.images[0] : defaultImg
          }
          alt="single room"
        />
        <div className="price-top">
          <h6>$ {room.pricePerDay}</h6>
          <p>per night</p>
        </div>
      </div>
      <p className="room-info">{room.typeRoom}</p>
      <p className="room-description">{room.description}</p>
      <Link to="/rooms" className="btn-primary">
        Back to rooms
      </Link>
    </div>
  );
}
