import React, { useState } from "react";

const PostingForm = () => {
  const [location, setLocation] = useState("");
  const [district, setDistrict] = useState("");
  const [address, setAddress] = useState("");

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
    updateDistrictOptions(e.target.value);
  };

  const handleDistrictChange = (e) => {
    setDistrict(e.target.value);
  };

  const handleAddressChange = (e) => {
    setAddress(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Xử lý logic đăng tin tại đây
    console.log("Địa chỉ:", address);
    console.log("Quận/Huyện:", district);
    console.log("Tỉnh/TP:", location);
  };

  const updateDistrictOptions = (location) => {
    // Cập nhật các tùy chọn Quận/Huyện dựa trên Tỉnh/TP được chọn
    if (location === "1") {
      setDistrict("");
      setDistrict((prevDistrict) => [
        ...prevDistrict,
        { value: "1", label: "Quận/Huyện 1" },
        { value: "2", label: "Quận/Huyện 2" },
      ]);
    } else if (location === "2") {
      setDistrict("");
      setDistrict((prevDistrict) => [
        ...prevDistrict,
        { value: "3", label: "Quận/Huyện 3" },
        { value: "4", label: "Quận/Huyện 4" },
      ]);
    } else {
      setDistrict([]);
    }
  };

  return (
    <div className="form-container">
      <h2>Đăng tin mới</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="location">Địa chỉ cho thuê:</label>
        <select id="location" value={location} onChange={handleLocationChange}>
          <option value="">-- Chọn Tỉnh/TP --</option>
          <option value="1">Tỉnh/TP 1</option>
          <option value="2">Tỉnh/TP 2</option>
        </select>

        <label htmlFor="district">Quận/Huyện:</label>
        <select id="district" value={district} onChange={handleDistrictChange}>
          <option value="">-- Chọn Quận/Huyện --</option>
        </select>

        <label htmlFor="address">Địa chỉ:</label>
        <input
          type="text"
          id="address"
          placeholder="Nhập địa chỉ"
          value={address}
          onChange={handleAddressChange}
        />

        <button type="submit">Đăng tin</button>
      </form>
    </div>
  );
};

export default PostingForm;
