import React, { useState } from "react";

const HotelRoomManager = () => {
  // Dữ liệu mẫu về các phòng
  const rooms = [
    { id: 101, type: "VIP" },
    { id: 102, type: "TRPL-MV" },
    { id: 103, type: "TRPL-SV" },
    { id: 104, type: "TWN-MV" },
    { id: 105, type: "TWN-SV" },
    { id: 106, type: "TRPL-MV" },
    { id: 107, type: "TRPL-SV" },
    { id: 108, type: "TRPL-MV" },
    { id: 109, type: "TRPL-SV" },
    { id: 201, type: "VIP" },
  ];

  // State để lưu trữ các giá trị của form
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");
  const [roomType, setRoomType] = useState("");

  return (
    <div className="hotel-room-manager">
      <div className="filters">
        <div className="date-filters">
          <label htmlFor="check-in">Check-in:</label>
          <input
            type="date"
            id="check-in"
            value={checkInDate}
            onChange={(e) => setCheckInDate(e.target.value)}
          />
          <label htmlFor="check-out">Check-out:</label>
          <input
            type="date"
            id="check-out"
            value={checkOutDate}
            onChange={(e) => setCheckOutDate(e.target.value)}
          />
        </div>
        <div className="room-type-filter">
          <label htmlFor="room-type">Room Type:</label>
          <select
            id="room-type"
            value={roomType}
            onChange={(e) => setRoomType(e.target.value)}
          >
            <option value="">All</option>
            <option value="VIP">VIP</option>
            <option value="TRPL-MV">TRPL-MV</option>
            <option value="TRPL-SV">TRPL-SV</option>
            <option value="TWN-MV">TWN-MV</option>
            <option value="TWN-SV">TWN-SV</option>
          </select>
        </div>
      </div>
      <div className="room-list">
        {rooms
          .filter((room) => roomType === "" || room.type === roomType)
          .map((room) => (
            <div key={room.id} className={`room-card room-type-${room.type}`}>
              {room.id}
            </div>
          ))}
      </div>
      <div className="add-room">
        <button>Add Room</button>
      </div>
    </div>
  );
};

export default HotelRoomManager;
