import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import defaultImg from "../../assets/img/jpeg/room-1.jpeg";

export default function Room({ roomIds }) {
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    if (roomIds.length > 0) {
      fetchRoomsByIds(roomIds);
    }
  }, [roomIds]);

  const fetchRoomsByIds = async (ids) => {
    try {
      const token = localStorage.getItem("token");
      const roomPromises = ids.map((id) =>
        fetch(`http://localhost:8080/hotelManagement/rooms/get-room/${id}`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }).then((response) => response.json())
      );
      const settledPromises = await Promise.allSettled(roomPromises);
      const validRoomsData = settledPromises
        .filter((promise) => promise.status === "fulfilled")
        .map((promise) => promise.value)
        .filter((room) => room && room.id);
      setRooms(validRoomsData);
    } catch (error) {
      console.error("Error fetching rooms:", error);
    }
  };

  return (
    <div className="room-list">
      {rooms.map((room) => (
        <article className="room" key={room.id}>
          <div className="img-container">
            <img
              src={
                room.images?.[0] ?? defaultImg // Optional chaining and nullish coalescing
              }
              alt="single room"
            />
            <div className="price-top">
              <h6>$ {room.pricePerDay}</h6>
              <p>per night</p>
            </div>
            <Link to={`/rooms/${room.id}`} className="btn-primary room-link">
              View
            </Link>
          </div>
          <p className="room-info">{room.typeRoom}</p>
        </article>
      ))}
    </div>
  );
}
