import React from "react";
import defaultImg from "../../assets/img/jpeg/room-1.jpeg";
export default function Room({ id }) {
  return (
    <article className="room">
      <div className="img-container">
        {/* Render the room image here */}
        <img src={defaultImg} alt="single room" />
      </div>
      <div className="room-info">
        <h4>Room {id}</h4>
        {/* Render other room details here */}
      </div>
    </article>
  );
}
