import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Banner from "../Components/Banner/Banner";
import StyledHero from "../Components/StyledHero/StyledHero";
export default function SingleRoom() {
  const { id } = useParams();
  const [room, setRoom] = useState(null);

  useEffect(() => {
    const fetchRoomData = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(
          `http://localhost:8080/hotelManagement/rooms/get-room/1`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        const data = await response.json();
        setRoom(data);
        console.log(data);
        console.log(id);
      } catch (error) {
        console.error("Error fetching room data:", error);
      }
    };

    fetchRoomData();
  }, [id]);

  if (!room) {
    return (
      <div className="error">
        <h3>no such room could be found!</h3>
        <Link to="/rooms" className="btn-primary">
          back to rooms
        </Link>
      </div>
    );
  }

  const { typeRoom, description, images, facilities, pricePerDay } = room;

  return (
    <>
      <StyledHero img={this.state.defaultBcg}>
        <Banner title={` room`}>
          <Link to="/rooms" className="btn-primary">
            back to rooms
          </Link>
        </Banner>
      </StyledHero>
      <section className="single-room">
        <div className="img-container">
          {images.map((img) => (
            <img key={img.id} src={img.image} alt={typeRoom} />
          ))}
        </div>
        <div className="single-room-info">
          <article className="desc">
            <h3>{typeRoom}</h3>
            <p>{description}</p>
          </article>
          <article className="info">
            <h6>Price: ${pricePerDay} per day</h6>
            <h6>Facilities:</h6>
            <ul className="facilities">
              {facilities.map((facility) => (
                <li key={facility.id}>{facility.facility}</li>
              ))}
            </ul>
          </article>
        </div>
      </section>
    </>
  );
}
