import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Banner from "../Components/Banner/Banner";
import StyledHero from "../Components/StyledHero/StyledHero";
import defaultBcg from "../assets/img/jpeg/room-1.jpeg";
import Flatpickr from "react-flatpickr";
import "flatpickr/dist/flatpickr.min.css";
export default function SingleRoom() {
  const { id } = useParams();
  const [room, setRoom] = useState(null);
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);

  const handleCheckInDateChange = (date) => {
    setCheckInDate(date);
  };

  const handleCheckOutDateChange = (date) => {
    setCheckOutDate(date);
  };

  useEffect(() => {
    const fetchRoomData = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(
          `http://localhost:8080/hotelManagement/rooms/get-room/${id}`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        const data = await response.json();
        setRoom(data);
        console.log(data);
        console.log(id);
      } catch (error) {
        console.error("Error fetching room data:", error);
      }
    };

    fetchRoomData();
  }, [id]);

  if (!room) {
    return (
      <div className="error">
        <h3>no such room could be found!</h3>
        <Link to="/rooms" className="btn-primary">
          back to rooms
        </Link>
      </div>
    );
  }

  const { typeRoom, description, images, facilities, pricePerDay } = room;

  return (
    <>
      <StyledHero img={defaultBcg}>
        <Banner title={` room`}>
          <Link to="/rooms" className="btn-primary">
            back to rooms
          </Link>
        </Banner>
      </StyledHero>
      <section className="single-room">
        <div className="single-room-images">
          {/* {images.map((img) => (
            <img key={img.roomId} src={images[2]} alt={"Photoo"} />
          ))} */}
          {images.map((image, index) => (
            <img key={index} src={image} alt={`Images ${index + 1}`} />
          ))}
        </div>
        <div className="single-room-info">
          <article className="room-description">
            <h3>{typeRoom}</h3>
            <p>{description}</p>
          </article>
          <article className="room-info">
            <h6>Price: ${pricePerDay} per day</h6>
            <h6>Facilities:</h6>
            <ul className="room-facilities">
              {facilities.map((facility, index) => (
                <li key={index}>{facility}</li>
              ))}
            </ul>
          </article>
        </div>
        <div className="booking-container">
          <div className="booking-dates">
            <div className="checkin-date">
              <label htmlFor="checkin-date">Check-in Date</label>
              <Flatpickr
                id="checkin-date"
                value={checkInDate}
                onChange={handleCheckInDateChange}
                options={{
                  dateFormat: "Y-m-d",
                  minDate: "today",
                }}
              />
            </div>
            <div className="checkout-date">
              <label htmlFor="checkout-date">Check-out Date</label>
              <Flatpickr
                id="checkout-date"
                value={checkOutDate}
                onChange={handleCheckOutDateChange}
                options={{
                  dateFormat: "Y-m-d",
                  minDate: "today",
                }}
              />
            </div>
          </div>
          <div className="book-now-container">
            <button className="book-now-btn">Book Now</button>
          </div>
        </div>
      </section>
    </>
  );
}
