import { useHistory } from "react-router-dom";
import React, { useState, useEffect } from "react";

const UserManage = () => {
  const [users, setUsers] = useState([]); // Initialize users as an empty array
  const [searchTerm, setSearchTerm] = useState("");
  const [error, setError] = useState(null);
  const history = useHistory();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async (searchTerm = "") => {
    try {
      const token = localStorage.getItem("token");
      const url = searchTerm
        ? `http://localhost:8080/hotelManagement/user/search?keyword=${encodeURIComponent(
            searchTerm
          )}`
        : "http://localhost:8080/hotelManagement/user/getAll";
      const response = await fetch(url, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();

      if (!response.ok) {
        throw new Error(
          data.msg || "Đã xảy ra lỗi khi lấy dữ liệu người dùng."
        );
      }

      setUsers(data.users);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleSearch = (e) => {
    const encodedSearchTerm = encodeURIComponent(e.target.value);
    setSearchTerm(e.target.value);
    fetchUsers(encodedSearchTerm);
  };

  const handleUpdate = (id) => {
    history.push(`/admin/update/${id}`);
  };

  const handleDelete = async (userName) => {
    // Handle delete user logic
  };

  const handleAddUser = () => {
    // Handle add user logic
    console.log("Add new user");
  };

  return (
    <div className="user-manage">
      <>
        <div className="user-manage-header">
          <h1>User Management</h1>
          <button className="btn-add-user" onClick={handleAddUser}>
            Add New User
          </button>
        </div>
        <div className="user-manage-search">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        {error && <div className="error">{error}</div>}
        <table className="user-manage-table">
          <thead>
            <tr>
              <th>Id</th>
              <th>Email</th>
              <th>Full Name</th>
              <th>Phone Number</th>
              <th>Address</th>
              <th>Role</th>
              <th>City</th>
              <th>Country</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.email}</td>
                  <td>{user.fullName}</td>
                  <td>{user.phoneNumber}</td>
                  <td>{user.address}</td>
                  <td>{user.role}</td>
                  <td>{user.city}</td>
                  <td>{user.country}</td>
                  <td>
                    <button
                      className="btn-update"
                      onClick={() => handleUpdate(user.id)}
                    >
                      Update
                    </button>
                    <button
                      className="btn-delete"
                      onClick={() => handleDelete(user.userName)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="9">No users found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </>
    </div>
  );
};

export default UserManage;
